import React from "react";
import { useForm, useFieldArray } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { useStateMachine } from "little-state-machine";
import styled from "styled-components";
import "./../styles/tailwind.css";
import jsonStore from "./../common/jsonsStore";
import updateAction from "./../common/updateAction";
import { FiMinusCircle, FiPlusCircle } from "react-icons/fi";

export default () => {
  const { state, action } = useStateMachine(updateAction);
  const { handleSubmit, errors, control, register, reset } = useForm({
    defaultValues: state.lookAtObjective,
  });

  const history = useHistory();
  const { push } = useHistory();
  const onSubmit = (data) => {
    action(data);
    history.push("/step2");
  };

  return (
    <FormContainer>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h3>Step-1</h3>
        {/* <h4 id="labeltitle">Look at Objective</h4> */}
        <label>
          Task Name:
          <input {...register("taskName")} />
        </label>
        <label>
          Objective Class:
          <input {...register("objectiveClass")} />
        </label>

        <input type="submit" value="&#x226A; Submit &#x226B;" />

        <input
          type="button"
          className="resetButton"
          value="Reset Selection"
          onClick={() => {
            reset(state);
            window.STATE_MACHINE_RESET();
            window.location.reload();
          }}
        />
      </form>
    </FormContainer>
  );
};

const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  flex-wrap: nowrap;
  justify-content: flex-start;
  align-content: flex-start;
  align-items: flex-start;
`;
const CheckSpacer = styled.div`
  border-top: 0px inset #10101080;
  width: 100%;
  margin: 2px 0;
`;
