import React from "react";
import { useFieldArray } from "react-hook-form";
import NestedArray from "./nestedFieldArray";
import NestedSD from "./nestedSubDescription";
import NestedBindEvent from "./nestedBindEvent";
import NestedVisible from "./nestedVisible";
import NestedPreActorControls from "./nestedPreActorControls";
import NestedPostActorControls from "./nestedPostActorControls";
import styled from "styled-components";
import "./../styles/tailwind.css";
import "./../styles/Custom.css";

let renderCount = 0;

export default function Fields({ control, register, setValue, getValues }) {
  const {
    fields: objFields,
    append: objAppend,
    remove: objRemove,
    prepend: objPrepend,
  } = useFieldArray({ control, name: "objectives" });

  // renderCount++;

  return (
    <>
      <ul>
        {objFields.map((item, index) => {
          return (
            <li key={item.id}>
              <label style={{ textAlign: "left" }}>Descriptions:</label>
              <input
                key={item.id}
                {...register(`objectives.${index}.description`)}
                defaultValue={item.description}
              />
              <NestedSD nestIndex={index} {...{ control, register }} />
              <NestedBindEvent nestIndex={index} {...{ control, register }} />
              <NestedVisible nestIndex={index} {...{ control, register }} />

              <label style={{ textAlign: "left", marginTop: "2em" }}>
                Objective Class:
              </label>
              <input
                key={item.id}
                {...register(`objectives.${index}.objectiveClass`)}
                defaultValue={item.objectiveClass}
              />

              <button
                className="buttonMinus"
                style={{ margin: "-.65em 0 1.5em" }}
                type="button"
                onClick={() => objRemove(index)}
              >
                Delete
              </button>
              <NestedArray nestIndex={index} {...{ control, register }} />
              <NestedPreActorControls
                nestIndex={index}
                {...{ control, register }}
              />
              <NestedPostActorControls
                nestIndex={index}
                {...{ control, register }}
              />
            </li>
          );
        })}
      </ul>

      <section>
        <button
          className="buttonPlus"
          style={{ marginBottom: "-5px" }}
          type="button"
          onClick={() => {
            objAppend({ name: "append" });
          }}
        >
          Append
        </button>

        <button
          className="buttonPlus"
          type="button"
          onClick={() => {
            setValue("objectives", [
              ...getValues().objectives,
              {
                actors: [{ actor: "append", presetState: "append" }],
              },
            ]);
          }}
        >
          Append Nested
        </button>

        <button
          className="buttonPlus"
          type="button"
          onClick={() => {
            objPrepend({ name: "append" });
          }}
        >
          Prepend
        </button>

        <button
          className="buttonPlus"
          style={{ marginTop: "-2px" }}
          type="button"
          onClick={() => {
            setValue("objectives", [
              {
                actors: [{ actor: "prepend", presetState: "prepend" }],
              },
              ...getValues().objectives,
            ]);
          }}
        >
          Prepend Nested
        </button>
      </section>

      <span className="counter">Render Count: {renderCount}</span>
    </>
  );
}

const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  flex-wrap: nowrap;
  justify-content: flex-start;
  align-content: flex-start;
  align-items: flex-start;
`;
const CheckSpacer = styled.div`
  border-top: 0px inset #10101080;
  width: 100%;
  margin: 2px 0;
`;
